import { generateM3U8, generateM3U8Variants } from 'utils/hlsUtil'

jest.mock('services/AudiusBackend', () => ({
  fetchCID: (multihash) => multihash
}))

describe('generate m3u8', () => {
  it('can generate an m3u8', () => {
    const m3u8 = generateM3U8([
      {
        multihash: 'Q111',
        duration: 5.99
      },
      {
        multihash: 'Q222',
        duration: 6.01
      }
    ])

    expect(m3u8).toEqual(
      `#EXTM3U
#EXT-X-VERSION:3
#EXT-X-TARGETDURATION:6
#EXT-X-MEDIA-SEQUENCE:0
#EXTINF:5.99
Q111
#EXTINF:6.01
Q222
#EXT-X-ENDLIST`
    )
  })

  it('can use a prefetched URL', () => {
    const m3u8 = generateM3U8(
      [
        {
          multihash: 'Q111',
          duration: 5.99
        },
        {
          multihash: 'Q222',
          duration: 6.01
        }
      ],
      ['blob:http://localhost:3000/Q111']
    )

    expect(m3u8).toEqual(
      `#EXTM3U
#EXT-X-VERSION:3
#EXT-X-TARGETDURATION:6
#EXT-X-MEDIA-SEQUENCE:0
#EXTINF:5.99
blob:http://localhost:3000/Q111
#EXTINF:6.01
Q222
#EXT-X-ENDLIST`
    )
  })

  it('uses a gateway override', () => {
    const m3u8 = generateM3U8(
      [
        {
          multihash: 'Q111',
          duration: 5.99
        },
        {
          multihash: 'Q222',
          duration: 6.01
        }
      ],
      [],
      'https://ipfs.io/ipfs/'
    )

    expect(m3u8).toEqual(
      `#EXTM3U
#EXT-X-VERSION:3
#EXT-X-TARGETDURATION:6
#EXT-X-MEDIA-SEQUENCE:0
#EXTINF:5.99
https://ipfs.io/ipfs/Q111
#EXTINF:6.01
https://ipfs.io/ipfs/Q222
#EXT-X-ENDLIST`
    )
  })
})

describe('generates m3u8 master with variants', () => {
  it('can generate an m3u8 master with variants', () => {
    const m3u8 = generateM3U8Variants(
      [
        {
          multihash: 'Q111',
          duration: 5.99
        },
        {
          multihash: 'Q222',
          duration: 6.01
        }
      ],
      [],
      ['https://ipfs.io/ipfs/', 'https://example.com/ipfs/']
    )

    expect(m3u8).toEqual(
      `data:application/vnd.apple.mpegURL;base64,`
    )
  })
})
